import React, { useState, useEffect } from 'react';
import Chart from 'react-google-charts';

const SiemPieChart = () => {
  const [pieChartData, setPieChartData] = useState({
    data: {
      rule_description: [],
      bg_count: [],
      Color_codes: [],
      status: ''
    }
  });

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch('/get-pie_chart');
        console.log(response.data);
        const contentType = response.headers.get('content-type');

        if (contentType && contentType.includes('application/json')) {
          const responseData = await response.json();

          if (responseData && responseData.status === 'Success') {
            const data = {
              data: {
                rule_description: responseData.data.rule_description,
                bg_count: responseData.data.bg_count,
                Color_codes: responseData.data.Color_codes,
                status: responseData.data.status,
              },
            };

            setPieChartData(data);
          } else {
            console.error('Error fetching pie chart data:', responseData);
            // Handle the case where the response is not in the expected format
          }
        } else {
          console.error('Non-JSON response:', response);
          // Handle the case where the response is not JSON
        }
      } catch (error) {
        console.error(error);
      }
    };

    fetchData();
  }, []);

  return (
    <div>
      <h2>Pie Chart</h2>
      <Chart
        width={'100%'}
        height={'400px'}
        chartType="PieChart"
        data={[
          ['Rule Description', 'Count'],
          ...pieChartData.data.rule_description.map((description, index) => [
            description,
            pieChartData.data.bg_count[index],
          ])
        ]}
        options={{
          title: 'Pie Chart',
          is3D: true,
        }}
        rootProps={{ 'data-testid': '1' }}
      />
    </div>
  );
};

export default SiemPieChart;
