import React, { useState, useEffect } from 'react';
import './wazuh.css';

const WazuhAgents = () => {
    const [agentsCount, setAgentsCount] = useState({
        total: 0,
        active: 0,
        inactive: 0,
        never_connected: 0,
    });

    useEffect(() => {
        fetchData();
    }, []);

    const fetchData = async () => {
        try {
            const response = await fetch("http://localhost:5500/agents_details.json");
            const agentsData = await response.json();

            // Calculate counts based on the status field in the JSON
            const total = agentsData.length;
            const active = agentsData.filter(agent => agent.status === 'active').length;
            const inactive = agentsData.filter(agent => agent.status === 'inactive').length;
            const neverConnected = agentsData.filter(agent => agent.status === 'disconnected').length;

            setAgentsCount({
                total,
                active,
                inactive,
                never_connected: neverConnected,
            });

        } catch (error) {
            console.error(error);
        }
    };

    return (
        <div>
            <ul style={{ display: "flex", flexDirection: "row", justifyContent: "space-around" }} className='px-1 py-3'>
                <li key={1} style={{ display: "flex", flexDirection: "column", boxShadow: "0px 5px 10px 1px slategray" }} className='agent'>
                    <span className='ps-5'>Total Agents</span>
                    <span className="data mb-5" style={{ borderTop: "0.5px grey solid" }}>{agentsCount.total}</span>
                </li>
                <li key={2} style={{ display: "flex", flexDirection: "column", boxShadow: "0px 5px 10px 1px slategray" }} className='agent'>
                    <span className="ps-5 ms-4" style={{}}>Active Agents</span>
                    <span className="data mb-5" style={{ borderTop: "0.5px grey solid" }}>{agentsCount.active}</span>
                </li>
                <li key={3} style={{ display: "flex", flexDirection: "column", boxShadow: "0px 5px 10px 1px slategray" }} className='agent'>
                    <span className="ps-5 ms-3">Inactive Agents</span>
                    <span className="data mb-5 " style={{ borderTop: "0.5px grey solid" }}>{agentsCount.inactive}</span>
                </li>
                <li key={4} style={{ display: "flex", flexDirection: "column", boxShadow: "0px 5px 10px 1px slategray" }} className='agent'>
                    <span className="ps-5 ms-4">Disconnected </span>
                    <span className="data mb-5" style={{ borderTop: "0.5px grey solid" }}>{agentsCount.never_connected}</span>
                </li>
            </ul>
        </div>
    );
};

export default WazuhAgents;
