import React, { useState, useEffect } from 'react';
import './wazuh.css';

function convert(date) {
  const newDate = new Date(date);
  const newDate2 = new Date(date);
  const options = {
    day: 'numeric',
    month: 'numeric',
    year: 'numeric',
  };

  const options2 = {
    hour: 'numeric',
    minute: 'numeric',
    hour12: true,
  };

  const array = [
    newDate.toLocaleDateString(undefined, options),
    newDate2.toLocaleTimeString(undefined, options2),
  ];
  return array;
}

const WazuhCombined = () => {
  const [data, setData] = useState({
    active: {
      total_count: 0,
      affected_items: [],
    },
    inactive: {
      total_count: 0,
      affected_items: [],
    },
    disconnected: {
      total_count: 0,
      affected_items: [],
    },
  });

  const [searchQuery, setSearchQuery] = useState('');

  useEffect(() => {
    fetchData('get-active_count', 'active');
    fetchData('get-inactive_count', 'inactive');
    fetchData('get-disconnected_count', 'disconnected');
  }, []);

  const fetchData = async (endpoint, key) => {
    try {
      const response = await fetch(`http://localhost:3001/${endpoint}`);
      const responseData = await response.json();
      
      const status = key; // Set status based on the key
      
      setData((prevData) => ({
        ...prevData,
        [key]: {
          total_count: responseData.data.total_count,
          affected_items: responseData.data.affected_items.map((item) => ({
            ...item,
            status: status, // Set the status for each item
          })),
        },
      }));
    } catch (error) {
      console.error(error);
    }
  };
  
  

  // Filter items based on the search query
  const filteredItems = data.active.affected_items
    .concat(data.inactive.affected_items)
    .concat(data.disconnected.affected_items)
    .filter((item) =>
      Object.values(item).some(
        (value) =>
          value &&
          value.toString().toLowerCase().includes(searchQuery.toLowerCase())
      )
    );

  // Function to highlight matched text
// Function to highlight matched text
// Function to highlight matched text
// Function to highlight matched text
// Function to highlight matched text
const highlightText = (text, highlight) => {
    if (!text || !highlight) {
      return text; // Return the original text if either text or highlight is undefined or null
    }
  
    // Escape special characters in the highlight for safe use in a regular expression
    const escapedHighlight = highlight.replace(/[.*+?^${}()|[\]\\]/g, '\\$&');
  
    const parts = text.split(new RegExp(`(${escapedHighlight})`, 'gi'));
    return (
      <span>
        {parts.map((part, index) =>
          part.toLowerCase() === escapedHighlight.toLowerCase() ? (
            <mark key={index}>{part}</mark>
          ) : (
            part
          )
        )}
      </span>
    );
  };
  
  


  return (
    <>
      {/* Search Bar */}
      <div className='inline-block m-3' style={{marginLeft:"500px"}}>
        <div className='form'>
          <div className='input-group w-50 ms-auto' style={{ marginLeft: '', marginBottom: '25px' }}>
            <input
              type='text'
              className='form-control'
              style={{
                background: '#fafaff',
                color: 'black',
                fontWeight: '500'
              }}
              placeholder='Search'
              id='searchbar'
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
            />
            <button type='submit' className='input-group-text' id='search-icon' style={{ background: 'rgba(46, 83, 218, 0.826)'}}>
              <svg xmlns='http://www.w3.org/2000/svg' width='16' height='16' fill='white' className='bi bi-search' viewBox='0 0 16 16'>
                <path d='M11.742 10.344a6.5 6.5 0 1 0-1.397 1.398h-.001c.03.04.062.078.098.115l3.85 3.85a1 1 0 0 0 1.415-1.414l-3.85-3.85a1.007 1.007 0 0 0-.115-.1zM12 6.5a5.5 5.5 0 1 1-11 0 5.5 5.5 0 0 1 11 0' />
              </svg>
            </button>
          </div>
        </div>
      </div>

      {/* Combined Table */}
      <table className='table '>
        <thead>
          <tr>
            <th>S.No</th>
            <th>OS</th>
            <th>Agent Name</th>
            <th>IP</th>
            <th className=''>Date</th>
            <th className=''>Time</th>
            <th className=''>Status</th>
          </tr>
        </thead>
        <tbody>
          {filteredItems.map((item, index) => (
            <tr key={index}>
              <td>{index + 1}</td>
              <td>{highlightText(item.os_name, searchQuery)}</td>
              <td>{highlightText(item.agent_name, searchQuery)}</td>
              <td>{highlightText(item.registerIP, searchQuery)}</td>
              <td className=''>{convert(item.dateAdd)[0]}</td>
              <td className=''>{convert(item.dateAdd)[1]}</td>
              <td className='status'>
                 {item.status === 'active' ? 'Active' : item.status === 'inactive' ? 'Inactive' : 'Disconnected'}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </>
  );
};

export default WazuhCombined;
