import React, { useState, useEffect } from 'react';
import Chart from 'react-google-charts';

const SiemPieChart = () => {
  const [pieChartData, setPieChartData] = useState({
    rule_description: [],
    bg_count: [],
    Color_codes: [],
    status: '',
  });

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      const response = await fetch("http://localhost:3001/get-pie_chart");
      const pieChartDataCount = await response.json();
      console.log(pieChartDataCount);
      setPieChartData(pieChartDataCount.data); // Access the nested "data" property
      console.log("Fetched Data:", pieChartDataCount);
    } catch (error) {
      console.error(error);
      
    }
  };

  const chartData = pieChartData.Color_codes
    ? [['Color', 'Count']].concat(
        pieChartData.Color_codes.map((color, index) => [
          color,
          pieChartData.bg_count[index],
        ])
      )
    : [];
    console.log("Updated Data:", chartData)

  return (
    <div>
      {pieChartData.status === 'Success' && pieChartData.Color_codes ? (
        <Chart
          width={'500px'}
          height={'300px'}
          chartType="PieChart"
          loader={<div>Loading Chart</div>}
          data={chartData}
          options={{
            title: 'Pie Chart',
          }}
          rootProps={{ 'data-testid': '1' }}
        />
      ) : (
        <p>Loading...</p>
      )}
    </div>
  );
};

export default SiemPieChart;
