import React, { useState, useEffect } from 'react';
import './wazuh.css';

const WazuhAgents = () => {
    const [agentsCount, setAgentsCount] = useState({
        data: {
            total: 0,
            active: 0,
            inactive: 0,
            neverConnected: 0,
        },
    });

    useEffect(() => {
        fetchData();
    }, []);

    const fetchData = async () => {
        try {
            const response = await fetch("http://localhost:3001/get-agents_count");
            const agentsCountData = await response.json();
            setAgentsCount(agentsCountData);

        } catch (error) {
            console.error(error);
        }
    };

    const agentData = [
        { key: 1, label: "Total Agents", count: agentsCount.data.total },
        { key: 2, label: "Active Agents", count: agentsCount.data.active },
        { key: 3, label: "Inactive Agents", count: agentsCount.data.inactive },
        { key: 4, label: "Disconnected", count: agentsCount.data.neverConnected },
    ];

    return (
        <div>
            <ul style={{ display: "flex", flexDirection: "row", justifyContent: "space-around" }} className='px-1 py-3'>
                {agentData.map((item) => (
                    <li key={item.key} style={{ display: "flex", flexDirection: "column", boxShadow: "0px 5px 10px 1px slategray" }} className='agent'>
                        <span className='ps-5' style={{ textAlign: "" }}>{item.label}</span>
                        <span className="data mb-5" style={{ borderTop: "0.5px grey solid" }}>
                            <svg xmlns="http://www.w3.org/2000/svg" width="75" height="75" fill="white" className="bi bi-person-fill-up pt-3" viewBox="0 0 16 30" style={{ background: "", marginTop: "-120px", marginLeft: "-80px", borderRadius: "21px" }}>
                                <path d="M12.5 16a3.5 3.5 0 1 0 0-7 3.5 3.5 0 0 0 0 7m.354-5.854 1.5 1.5a.5.5 0 0 1-.708.708L13 11.707V14.5a.5.5 0 0 1-1 0v-2.793l-.646.647a.5.5 0 0 1-.708-.708l1.5-1.5a.5.5 0 0 1 .708 0M11 5a3 3 0 1 1-6 0 3 3 0 0 1 6 0" />
                                <path d="M2 13c0 1 1 1 1 1h5.256A4.493 4.493 0 0 1 8 12.5a4.49 4.49 0 0 1 1.544-3.393C9.077 9.038 8.564 9 8 9c-5 0-6 3-6 4" />
                            </svg>{item.count}
                        </span>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default WazuhAgents;
