import React, { useState, useEffect } from 'react';
import './wazuh.css';

const WazuhAgents = () => {
    const [agentsCount, setAgentsCount] = useState({
        data: {
            total: 0,
            active: 0,
            inactive: 0,
            neverConnected: 0,
        },
    });

    useEffect(() => {
        fetchData();
    }, []);

    const fetchData = async () => {
        try {
            const response = await fetch("http://localhost:3001/get-agents_count");
            const agentsCountData = await response.json();
            setAgentsCount(agentsCountData);

        } catch (error) {
            console.error(error);
        }
    };

    return (
        <div>
                <ul style={{display:"flex",flexDirection:"row",justifyContent:"space-around"}} className='px-1 py-3'>
                    <li key={1} style={{display:"flex",flexDirection:"column"}} className='agent'><svg xmlns="http://www.w3.org/2000/svg" width="160" height="160" fill="currentColor" class="bi bi-person-fill" viewBox="0 0 16 16">
  <path d="M3 14s-1 0-1-1 1-4 6-4 6 3 6 4-1 1-1 1zm5-6a3 3 0 1 0 0-6 3 3 0 0 0 0 6"/>
</svg>Total Agents <span className="data"> {agentsCount.data[0]?.total}</span></li>
                    <li key={2}  style={{display:"flex",flexDirection:"column"}} className='agent'>Active Agents <span className="data">{agentsCount.data[0]?.active}</span></li>
                    <li key={3}  style={{display:"flex",flexDirection:"column"}} className='agent'>Inactive Agents<span className="data"> {agentsCount.data[0]?.inactive}</span></li>
                    <li key={4} style={{display:"flex",flexDirection:"column"}} className='agent'>Disconnected <span className="data">{agentsCount.data[0]?.never_connected}</span></li>
                </ul>
 
        </div>
    );
};

export default WazuhAgents;
