import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import dashboard from '../components/images/dashobard.jpg';
import { useAuth } from '../AuthContext';

function Register() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();
  const { login } = useAuth();

  const handleRegister = async (e) => {
    e.preventDefault();

    try {
      const response = await fetch('http://localhost:3001/auth/register', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ email, password }),
      });

      const data = await response.json();

      if (response.ok) {
        console.log('User registered successfully');
        login(); // Mark the user as authenticated after registration
        navigate('/login'); // Redirect to login page or any other route
      } else {
        console.error(data.error || 'Registration failed');
      }
    } catch (error) {
      console.error('Error during registration:', error);
    }
  };

  return (
    <div className='container-fluid align-items-center justify-content-center p-0 m-0' style={{ position: "absolute", zIndex: "2", height: "1000px" }}>
      <center className='h4'><h2 style={{ fontWeight: "800", paddingTop: "20px" }}>Welcome</h2><br />&nbsp;New User? Register Now.</center>
      <img src={dashboard} alt="" className='' style={{ width: "1480px", height: "800px", zIndex: "-2", position: "absolute", top: "0", objectFit: "cover" }} />
      <div className='form w-25 ps-3' style={{ marginTop: "200px", marginLeft: "540px" }}>
        <form onSubmit={handleRegister}>
          <label htmlFor="username">Email</label>
          <input type="email" id="username" className='form-control py-2 my-1' placeholder='abc@example.com' onChange={(e) => setEmail(e.target.value)} />
          <br />
          <label htmlFor="password">Password</label>
          <input type="password" id="password" className='form-control py-2 my-1' placeholder='Password' onChange={(e) => setPassword(e.target.value)} />
          <button type='submit' className='p-2 mb-3 text-center align-items-center btn btn-primary' style={{ marginLeft: "135px", marginTop: "24px" }}>Register</button>
          <br />
          <Link className='ps-4 ms-5' to="/login">Have an account? Login</Link>
        </form>
      </div>
    </div>
  );
}

export default Register;
