import React, { useState } from 'react';
import Navbar from '../components/navbar';
import './Home.css';
import SiemPieChart from '../components/siem-piechart';
import SiemBarChart from '../components/siem-barchart';
import { Link } from 'react-router-dom';

function SIEM() {
  const [sidebarOpen, setSidebarOpen] = useState(false);

  const handleToggleSidebar = () => {
    setSidebarOpen(!sidebarOpen);
  };

  return (
    <div className='container-fluid p-0' style={{ marginTop: '20px', maxHeight: '1560px', background: 'rgb(240,242,245)' }}>
      <div className="row">
        <div className="col-2">
          <Navbar />
        </div>

        <div className='col-10'>
          <span className='ps-5  '><b>Pages</b> /SIEM</span>
          <button type='button' className='btn btn-primary' onClick={handleToggleSidebar} style={{ marginLeft: '1000px' }}>
            Alerts
          </button>

          <div className="charts-container mt-5">
            <SiemPieChart />
            <SiemBarChart />
          </div>
          <h3 className='' style={{ marginLeft: '510px' }}>Alerts</h3>

          {sidebarOpen && (
            <div className="list-group-sidebar">
              <Link to="/" className="list-group-item list-group-item-action" aria-current="true">
                {/* ... */}
              </Link>
              {/* Add more list group items as needed */}
              <button className="close-button" onClick={handleToggleSidebar}>
                Close
              </button>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

export default SIEM;
