import React, { useState, useEffect } from 'react';

const SiemPieChart = () => {
  const [pieChartData, setPieChartData] = useState({
    data: {
      rule_description: [],
      bg_count: [],
      Color_codes: [],
      status: '',
    },
  });

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      const response = await fetch("http://localhost:3001/get-pie_chart");
      const pieChartDataCount = await response.json();
      console.log(pieChartDataCount);
      setPieChartData(pieChartDataCount);
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div>
      {pieChartData.Color_codes ? (
        <ul>
          {pieChartData.Color_codes.map((color, index) => (
            <li key={index}>{color}</li>
          ))}
        </ul>
      ) : (
        <p>Loading...</p>
      )}
    </div>
  );
};

export default SiemPieChart;
