import React, { useState, useEffect } from 'react';
import Chart from 'react-google-charts';

const SiemPieChart = () => {
    const [pieChartData, setPieChartData] = useState({
        data:{
                rule_description:[''],
                bg_count:[''],
                Color_codes: [''],
                status:''
        }
    });
  
    useEffect(() => {
      const fetchData = async () => {
        try {
          const response = await fetch('/get-pie_chart');
          const responseData = await response.json();
          console.log(responseData);
  
          if (responseData.status === 'Success') {
            const data = responseData.data.rule_description.map((description, index) => [
              description,
              responseData.data.bg_count[index],
            ]);
  
            setPieChartData([['Rule Description', 'Count'], ...data]);
          } else {
            console.error('Error fetching pie chart data');
          }
        } catch (error) {
          console.error(error);
        }
      };
  
      fetchData();
    }, []);
  
    return (
      <div>
        <h2>Pie Chart</h2>
        <Chart
          width={'100%'}
          height={'400px'}
          chartType="PieChart"
          data={pieChartData}
          options={{
            title: 'Pie Chart',
            is3D: true,
          }}
          rootProps={{ 'data-testid': '1' }}
        />
      </div>
    );
  };
  
  export default SiemPieChart;
