import React, { useState, useEffect } from 'react';
import { Chart } from 'react-google-charts';
import '../loader'

const SiemPieChart = () => {
  const [pieChartData, setPieChartData] = useState({
    rule_description: [],
    bg_count: [],
    Color_codes: [],
    status: '',
  });

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      const response = await fetch("http://localhost:3001/get-pie_chart");
      const pieChartDataCount = await response.json();
      console.log(pieChartDataCount);
      setPieChartData(pieChartDataCount);
    } catch (error) {
      console.error(error);
    }
  };

  const chartData = [['Color', 'Count']].concat(
    pieChartData.rule_description.map((color, index) => [color.toString().split(" ").map((word) => { 
        return word[0].toUpperCase() + word.substring(1); 
    }).join(" "), pieChartData.bg_count[index]])
  );

  return (
    <div>
      {pieChartData.Color_codes ? (
        <Chart
          width={'600px'}
          height={'500px'}
          chartType="PieChart"
          loader={<div className='h4'>Loading Chart..</div>}
          data={chartData}
          options={{
            title: 'SIEM Alerts',
          }}
          rootProps={{ 'data-testid': '1' }}
        />
      ) : (
        <p>Loading...</p>
      )}
    </div>
  );
};

export default SiemPieChart;
