import React, { useState, useEffect } from 'react';
import './wazuh.css';

// Helper function for date conversion
function convert(date) {
  const newDate = new Date(date);
  const newDate2 = new Date(date);
  const options = {
    day: 'numeric',
    month: 'numeric',
    year: 'numeric',
  };

  const options2 = {
    hour: 'numeric',
    minute: 'numeric',
    hour12: true,
  };

  const array = [
    newDate.toLocaleDateString(undefined, options),
    newDate2.toLocaleTimeString(undefined, options2),
  ];
  return array;
}

let globalIndex = 0; // Initialize global counter

const WazuhTable = ({ data, endpoint }) => {
  return (
    <>
      {data.affected_items.map((item) => (
        <tr key={globalIndex++}>
          <td>{globalIndex}</td>
          <td>{item.os_name}</td>
          <td>{item.agent_name}</td>
          <td>{item.registerIP}</td>
          <td>{convert(item.dateAdd)[0]}</td>
          <td>{convert(item.dateAdd)[1]}</td>
          <td>
            {(() => {
              switch (endpoint) {
                case 'get-active_count':
                  return 'Active';
                case 'get-inactive_count':
                  return 'Inactive';
                case 'get-disconnected_count':
                  return 'Disconnected';
                default:
                  return '';
              }
            })()}
          </td>
        </tr>
      ))}
    </>
  );
};

const WazuhCombined = () => {
  const [combinedData, setCombinedData] = useState({
    'get-active_count': { data: { affected_items: [] } },
    'get-inactive_count': { data: { affected_items: [] } },
    'get-disconnected_count': { data: { affected_items: [] } },
  });

  useEffect(() => {
    fetchData('get-active_count');
    fetchData('get-inactive_count');
    fetchData('get-disconnected_count');
  }, []);

  const fetchData = async (endpoint) => {
    try {
      const response = await fetch(`http://localhost:3001/${endpoint}`);
      const responseData = await response.json();
      setCombinedData((prevData) => ({
        ...prevData,
        [endpoint]: responseData,
      }));
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div>
      <h1>Wazuh Data</h1>
      <table className="table table-info">
        <thead>
          <tr>
            <th id="tab">S.No</th>
            <th>OS</th>
            <th className="">Agent Name</th>
            <th className=" ">IP</th>
            <th className=" ">Date</th>
            <th className=" ">Time</th>
            <th className=" ">Status</th>
          </tr>
        </thead>
        <tbody>
          <WazuhTable data={combinedData['get-active_count'].data} endpoint="get-active_count" />
          <WazuhTable data={combinedData['get-inactive_count'].data} endpoint="get-inactive_count" />
          <WazuhTable data={combinedData['get-disconnected_count'].data} endpoint="get-disconnected_count" />
        </tbody>
      </table>
    </div>
  );
};

export default WazuhCombined;
