import React, { useState, useEffect } from 'react';
import './wazuh.css';
// import {BsPersonFill} from 'react-icons/bs';


const WazuhAgents = () => {
    const [agentsCount, setAgentsCount] = useState({
        data: {
            total: 0,
            active: 0,
            inactive: 0,
            neverConnected: 0,
        },
    });

    useEffect(() => {
        fetchData();
    }, []);

    const fetchData = async () => {
        try {
            const response = await fetch("http://localhost:3001/get-agents_count");
            const agentsCountData = await response.json();
            setAgentsCount(agentsCountData);

        } catch (error) {
            console.error(error);
        }
    };

    return (
        <div>
                <ul style={{display:"flex",flexDirection:"row",justifyContent:"space-around"}} className='px-1 py-3'>
                    <li key={1} style={{display:"flex",flexDirection:"column",boxShadow:"0px 5px 10px 1px slategray"}} className='agent'>{/*<h2><BsPersonFill style={{height:"55px",width:"40px"}} /></h2>*/}<span className='ps-5' style={{textAlign:""}}>Total Agents</span> <span className="data mb-5" style={{borderTop:"0.5px grey solid"}}><svg xmlns="http://www.w3.org/2000/svg" width="45" height="45" fill="white" className="bi bi-person-fill-up" viewBox="0 0 16 30" style={{background:"",marginTop:"-120px",marginLeft:"-80px",borderRadius:"25px"}}>
  <path d="M12.5 16a3.5 3.5 0 1 0 0-7 3.5 3.5 0 0 0 0 7m.354-5.854 1.5 1.5a.5.5 0 0 1-.708.708L13 11.707V14.5a.5.5 0 0 1-1 0v-2.793l-.646.647a.5.5 0 0 1-.708-.708l1.5-1.5a.5.5 0 0 1 .708 0M11 5a3 3 0 1 1-6 0 3 3 0 0 1 6 0"/>
  <path d="M2 13c0 1 1 1 1 1h5.256A4.493 4.493 0 0 1 8 12.5a4.49 4.49 0 0 1 1.544-3.393C9.077 9.038 8.564 9 8 9c-5 0-6 3-6 4"/>
</svg>54 {agentsCount.data[0]?.total}</span></li>
                    <li key={2}  style={{display:"flex",flexDirection:"column"}} className='agent'>Active Agents <span className="data"><svg xmlns="http://www.w3.org/2000/svg" width="70" height="70" fill="currentColor" class="bi bi-person-fill-up" viewBox="0 0 16 19">
  <path d="M12.5 16a3.5 3.5 0 1 0 0-7 3.5 3.5 0 0 0 0 7m.354-5.854 1.5 1.5a.5.5 0 0 1-.708.708L13 11.707V14.5a.5.5 0 0 1-1 0v-2.793l-.646.647a.5.5 0 0 1-.708-.708l1.5-1.5a.5.5 0 0 1 .708 0M11 5a3 3 0 1 1-6 0 3 3 0 0 1 6 0"/>
  <path d="M2 13c0 1 1 1 1 1h5.256A4.493 4.493 0 0 1 8 12.5a4.49 4.49 0 0 1 1.544-3.393C9.077 9.038 8.564 9 8 9c-5 0-6 3-6 4"/>
</svg>{agentsCount.data[0]?.active}1</span></li>
                    <li key={3}  style={{display:"flex",flexDirection:"column"}} className='agent'>Inactive Agents<span className="data"> {agentsCount.data[0]?.inactive}48</span></li>
                    <li key={4} style={{display:"flex",flexDirection:"column"}} className='agent'>Disconnected <span className="data">{agentsCount.data[0]?.never_connected}5</span></li>
                </ul>
        
        </div>
    );
};

export default WazuhAgents;
