import React, { useState, useEffect } from 'react';
import './wazuh.css';
// import {BsPersonFill} from 'react-icons/bs';

const WazuhAgents = () => {
    const [agentsCount, setAgentsCount] = useState({
        data: {
            total: 0,
            active: 0,
            inactive: 0,
            neverConnected: 0,
        },
    });

    useEffect(() => {
        fetchData();
    }, []);

    const fetchData = async () => {
        try {
            const response = await fetch("http://localhost:3001/get-agents_count");
            const agentsCountData = await response.json();
            setAgentsCount(agentsCountData);
        } catch (error) {
            console.error(error);
        }
    };

    const commonStyle = {
        width: "80px",
        height: "80px",
        fill: "white",
        borderRadius: "21px",
        marginTop: "-120px",
        marginLeft: "-80px",
    };

    return (
        <div>
            <ul style={{ display: "flex", flexDirection: "row", justifyContent: "space-around" }} className='px-1 py-3'>
                {[1, 2, 3, 4].map((key) => (
                    <li key={key} style={{ display: "flex", flexDirection: "column", boxShadow: "0px 5px 10px 1px slategray" }} className='agent'>
                        {key === 1 ? "Total Agents" : key === 2 ? "Active Agents" : key === 3 ? "Inactive Agents" : "Disconnected"}
                        <span className="data" style={{ borderTop: "0.5px grey solid" }}>
                            {key === 1 && (
                                <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 16 30" style={{ ...commonStyle }}>
                                    <path d="M12.5 16a3.5 3.5 0 1 0 0-7 3.5 3.5 0 0 0 0 7m.354-5.854 1.5 1.5a.5.5 0 0 1-.708.708L13 11.707V14.5a.5.5 0 0 1-1 0v-2.793l-.646.647a.5.5 0 0 1-.708-.708l1.5-1.5a.5.5 0 0 1 .708 0M11 5a3 3 0 1 1-6 0 3 3 0 0 1 6 0" />
                                    <path d="M2 13c0 1 1 1 1 1h5.256A4.493 4.493 0 0 1 8 12.5a4.49 4.49 0 0 1 1.544-3.393C9.077 9.038 8.564 9 8 9c-5 0-6 3-6 4" />
                                </svg>
                            )}
                            {key === 2 && (
                                <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 16 19" style={{ ...commonStyle }}>
                                    <path d="M12.5 16a3.5 3.5 0 1 0 0-7 3.5 3.5 0 0 0 0 7m.354-5.854 1.5 1.5a.5.5 0 0 1-.708.708L13 11.707V14.5a.5.5 0 0 1-1 0v-2.793l-.646.647a.5.5 0 0 1-.708-.708l1.5-1.5a.5.5 0 0 1 .708 0M11 5a3 3 0 1 1-6 0 3 3 0 0 1 6 0" />
                                    <path d="M2 13c0 1 1 1 1 1h5.256A4.493 4.493 0 0 1 8 12.5a4.49 4.49 0 0 1 1.544-3.393C9.077 9.038 8.564 9 8 9c-5 0-6 3-6 4" />
                                </svg>
                            )}
                            {key === 3 && (
                                <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 16 19" style={{ ...commonStyle }}>
                                    {/* Custom SVG for Inactive Agents */}
                                    {/* Replace the path data with your desired SVG path */}
                                    <path d="M10 7h1v1h-1V7zm-2 0h1v1H8V7zm-2 0h1v1H6V7zm5 2H5v10h8V9zm1-7a3 3 0 0 0-6 0H1v16h14V2h-3z" />
                                </svg>
                            )}
                            {key === 4 && (
                                <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 16 19" style={{ ...commonStyle }}>
                                    {/* Custom SVG for Disconnected */}
                                    {/* Replace the path data with your desired SVG path */}
                                    <path d="M10 7h1v1h-1V7zm-2 0h1v1H8V7zm-2 0h1v1H6V7zm5 2H5v10h8V9zm1-7a3 3 0 0 0-6 0H1v16h14V2h-3z" />
                                </svg>
                            )}
                            {key === 1 ? 54 : key === 2 ? agentsCount.data[0]?.active : key === 3 ? agentsCount.data[0]?.inactive : agentsCount.data[0]?.never_connected}
                        </span>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default WazuhAgents;
