import React,{useState,useEffect} from 'react';

const WazuhActive = () => {
    const [activeCount,setActiveCount] = useState({
        data:{
            total_count: 0,
            affected_items: [{os_name:"",agent_name:"",registerIP:"",dateAdd:""}]
        }
    });


    useEffect(() => {
        fetchData();
    }, []);

    const fetchData = async () =>{
        try {
                const response = await fetch("http://localhost:3001/get-active_count");
                const activeCountData = await response.json();
                console.log(activeCountData);
                setActiveCount(activeCountData);

        } catch(error){
            console.error(error);
        }

    };


    return(
        <div>
            <h4 className="px-3">&gt;&nbsp;Active Users: {activeCount.data.total_count}</h4>
            
                {/* <svg style={{display:"inline",marginLeft:"85px"}} xmlns="http://www.w3.org/2000/svg" width="120" height="120" fill="currentColor" class="bi bi-person-fill" viewBox="0 0 16 16">
                        <path d="M3 14s-1 0-1-1 1-4 6-4 6 3 6 4-1 1-1 1zm5-6a3 3 0 1 0 0-6 3 3 0 0 0 0 6"/>
                </svg> */}
                <table border>
                    <tr>
                           <td>OS</td> 
                           <td>Agent Name</td> 
                           <td>IP</td>
                           <td>Date</td>
                        
                    </tr>
                    <tr>
                    <ul style={{display:"flex",paddingLeft:"0px",marginLeft:"10px",padding:"1rem",flexWrap:"wrap",justifyContent:"center"}}>
                    {activeCount.data.affected_items.map((item, index) => (
                            <li key={index} className='p-5 ' style={{listStyle:"none",display:"flex",flexDirection:"column",backgroundColor:"orange",margin:"5px",maxWidth:"395px"}}>
                              <td>{item.os_name} </td> 
                               <td>{item.agent_name}</td>
                              Registered IP:{item.registerIP} 
                              Date Added: {item.dateAdd} 
                            </li>
                        ))}
                    </ul>
                    </tr>
                </table>
        </div>
    )
}


export default WazuhActive
