import React, { useState, useEffect } from 'react';
import { Pie } from 'react-chartjs-2';

const SiemPieChart = () => {
  const [pieChartData, setPieChartData] = useState({
    labels: [],
    datasets: [
      {
        data: [],
        backgroundColor: [],
      },
    ],
  });

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch('/get-pie_chart');
        const responseData = await response.json();

        if (responseData.status === 'Success') {
          const data = {
            labels: responseData.data.rule_description,
            datasets: [
              {
                data: responseData.data.bg_count,
                backgroundColor: responseData.data.Color_codes,
              },
            ],
          };

          setPieChartData(data);
        } else {
          console.error('Error fetching pie chart data');
        }
      } catch (error) {
        console.error(error);
      }
    };

    fetchData();
  }, []);

  return (
    <div>
      <h2>Pie Chart</h2>
      <Pie data={pieChartData} />
    </div>
  );
};

export default SiemPieChart;
