import React, { useState } from 'react';
import { Link, useHistory } from 'react-router-dom';
// import dashboard from '../components/images/dashobard.jpg';

function Login() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const history = useHistory();

  const handleLogin = async (e) => {
    e.preventDefault();

    try {
      const response = await fetch('http://localhost:3001/auth/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ username: email, password }),
      });

      const data = await response.json();

      if (response.ok) {
        console.log('User logged in successfully');
        // Redirect to dashboard or any other route
        history.push('/dashboard');
      } else {
        console.error(data.error || 'Login failed');
      }
    } catch (error) {
      console.error('Error during login:', error);
    }
  };

  return (
    <div className='container-fluid align-items-center justify-content-center p-0 m-0' style={{ position: "absolute", zIndex: "2", height: "1000px" }}>
      {/* ... rest of the component code ... */}
      <div className='form w-25 ps-3' style={{ marginTop: "200px", marginLeft: "540px" }}>
        <form onSubmit={handleLogin}>
          {/* ... rest of the form code ... */}
          <button type='submit' className='p-2 mb-3 text-center align-items-center btn btn-primary' style={{ marginLeft: "140px", marginTop: "24px" }}>Login</button>
        </form>
      </div>
    </div>
  );
}

export default Login;
