import React, { useState } from 'react';
import Navbar from '../components/navbar';
import SiemPieChart from '../components/siem-piechart';
import SiemBarChart from '../components/siem-barchart';
import { Link } from 'react-router-dom';
import './Home.css';

function SIEM() {
  const [showAlerts, setShowAlerts] = useState(true);

  return (
    <div className='container-fluid p-0' style={{ marginTop: '20px', maxHeight: '1560px', background: 'rgb(240,242,245)' }}>
      <div className='row'>
        <div className='col-2'>
          <Navbar />
        </div>

        <div className='col-10'>
          <span className='ps-5 '>
            <b>Pages</b> /SIEM
          </span>
          <button
            type='button'
            className={`btn btn-primary ${showAlerts ? 'active' : ''}`}
            style={{ marginLeft: '1000px' }}
            onClick={() => setShowAlerts(!showAlerts)}
          >
            {showAlerts ? 'Hide Alerts' : 'Show Alerts'}
          </button>

          <div className={`charts-container mt-5 ${showAlerts ? 'fade-in' : 'fade-out'}`}>
            <SiemPieChart />
            <SiemBarChart />
          </div>
          <h3 className='' style={{ marginLeft: '510px' }}>Alerts</h3>

          {showAlerts && (
            <div className={`list-group w-25 ps-5 collapse ${showAlerts ? 'show' : ''}`}>
              <div class='list-group-item list-group-item-action'>
                <div>
                  <h5 class='mb-1'>List group item heading</h5>
                  <p class='mb-1'>Some placeholder content in a paragraph.</p>
                  <small>And some small print.</small>
                </div>
              </div>
              <div class='list-group-item list-group-item-action'>
                <div>
                  <h5 class='mb-1'>List group item heading</h5>
                  <p class='mb-1'>Some placeholder content in a paragraph.</p>
                  <small class='text-body-secondary'>And some muted small print.</small>
                </div>
              </div>
              <div class='list-group-item list-group-item-action'>
                <div>
                  <h5 class='mb-1'>List group item heading</h5>
                  <p class='mb-1'>Some placeholder content in a paragraph.</p>
                  <small class='text-body-secondary'>And some muted small print.</small>
                </div>
              </div>
            </div>
          )}
          <button
            type='button'
            className={`btn btn-secondary ${showAlerts ? 'fade-in' : 'fade-out'}`}
            style={{ marginLeft: '940px', marginTop: '-550px' }}
            onClick={() => setShowAlerts(false)}
          >
            Close Alerts
          </button>
        </div>
      </div>
    </div>
  );
}

export default SIEM;
