import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import dashboard from '../components/images/dashobard.jpg';
import { useAuth } from '../AuthContext';

function Login() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();
  const { login } = useAuth();

  const handleLogin = async (e) => {
    e.preventDefault();

    try {
// Login.jsx
        const response = await fetch('http://localhost:3001/auth/login', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({ email, password }),
        });


      const data = await response.json();

      if (response.ok) {
        console.log('User logged in successfully');
        login(); // Mark the user as authenticated
        navigate('/dashboard'); // Redirect to dashboard or any other route
      } else {
        console.error(data.error || 'Login failed');
      }
    } catch (error) {
      console.error('Error during login:', error);
    }
  };

  return (
    <div className='container-fluid align-items-center justify-content-center p-0 m-0' style={{ position: "absolute", zIndex: "2", height: "1000px" }}>
      <center className='h4'><h2 style={{ fontWeight: "800", paddingTop: "20px" }}>Welcome</h2><br />&nbsp;Login into Dashboard</center>
      <img src={dashboard} alt="" className='' style={{ width: "1480px", height: "800px", zIndex: "-2", position: "absolute", top: "0", objectFit: "cover" }} />
      <div className='form w-25 ps-3' style={{ marginTop: "200px", marginLeft: "540px" }}>
        <form onSubmit={handleLogin}>
          <label htmlFor="username">Email</label>
          <input type="email" id="username" className='form-control py-2 my-1' placeholder='abc@example.com' onChange={(e) => setEmail(e.target.value)} />
          <br />
          <label htmlFor="password">Password</label>
          <input type="password" id="password" className='form-control py-2 my-1' placeholder='Password' onChange={(e) => setPassword(e.target.value)} />
          <button type='submit' className='p-2 mb-3 text-center align-items-center btn btn-primary' style={{ marginLeft: "140px", marginTop: "24px" }}>Login</button>
          <br />
          <Link className='ps-1 ms-5' to="/register">Do not have an account? Register</Link>
        </form>
      </div>
    </div>
  );
}

export default Login;
