import React, { useState, useEffect } from 'react';
import Chart from 'react-google-charts';

const SiemBarChart = () => {
  const [barChartData, setBarChartData] = useState({
    data: [],
  });

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      const response = await fetch("http://localhost:3001/get-bar_chart");
      const barChartDataCount = await response.json();
      setBarChartData(barChartDataCount); // No need for .data here
      console.log("Fetched Bar Data:", barChartDataCount);
    } catch (error) {
      console.error(error);
    }
  }

  const barChartDetails = [
    ['Agent Name', 'Rules'],
    ...barChartData.data.map(entry => [entry['agent-name'], entry['number-of-rules']])
  ];

  return (
    <div>
      <Chart
        width={"500px"}
        height={"400px"}
        chartType='BarChart'
        data={barChartDetails}
        options= {{
            backgroundColor:"transparent"
        }}

        colors = {barChartData.map}
        
      />
    </div>
  );
};

export default SiemBarChart;
