import React, { useState, useEffect } from 'react';
import WazuhTable from './WazuhTable';  // Make sure to adjust the path
import './wazuh.css';

const WazuhCombined = () => {
  const [activeData, setActiveData] = useState([]);
  const [inactiveData, setInactiveData] = useState([]);
  const [disconnectedData, setDisconnectedData] = useState([]);

  useEffect(() => {
    fetchData('get-active_count', setActiveData);
    fetchData('get-inactive_count', setInactiveData);
    fetchData('get-disconnected_count', setDisconnectedData);
  }, []);

  const fetchData = async (endpoint, setData) => {
    try {
      const response = await fetch(`http://localhost:3001/${endpoint}`);
      const responseData = await response.json();
      setData(responseData.data);  // Assuming the data structure has a 'data' property
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div>
      <h1>Wazuh Data</h1>
      <table className="table table-info">
        <thead>
          <tr>
            <th id="tab">S.No</th>
            <th>OS</th>
            <th className="">Agent Name</th>
            <th className=" ">IP</th>
            <th className=" ">Date</th>
            <th className=" ">Time</th>
            <th className=" ">Status</th>
          </tr>
        </thead>
        <tbody>
          <WazuhTable data={activeData} endpoint="get-active_count" />
          <WazuhTable data={inactiveData} endpoint="get-inactive_count" />
          <WazuhTable data={disconnectedData} endpoint="get-disconnected_count" />
        </tbody>
      </table>
    </div>
  );
};

export default WazuhCombined;
