const WazuhCombined = () => {
  const [data, setData] = useState({
    active: {
      total_count: 0,
      affected_items: [],
    },
    inactive: {
      total_count: 0,
      affected_items: [],
    },
    disconnected: {
      total_count: 0,
      affected_items: [],
    },
  });

  const [searchQuery, setSearchQuery] = useState('');

  useEffect(() => {
    fetchData('get-active_count', 'active', 'active');
    fetchData('get-inactive_count', 'inactive', 'inactive');
    fetchData('get-disconnected_count', 'disconnected', 'disconnected');
  }, []);

  const fetchData = async (endpoint, key, status) => {
    try {
      const response = await fetch(`http://localhost:3001/${endpoint}`);
      const responseData = await response.json();
      setData((prevData) => ({
        ...prevData,
        [key]: { ...responseData.data, status },
      }));
    } catch (error) {
      console.error(error);
    }
  };

  // Filter items based on the search query
  const filteredItems = data.active.affected_items
    .concat(data.inactive.affected_items)
    .concat(data.disconnected.affected_items)
    .filter((item) =>
      Object.values(item).some(
        (value) =>
          value &&
          value.toString().toLowerCase().includes(searchQuery.toLowerCase())
      )
    );

  // ... (rest of the code remains the same)

  return (
    <>
      {/* ... (rest of the code remains the same) */}

      {/* Combined Table */}
      <table className='table'>
        <thead>
          <tr>
            <th>S.No</th>
            <th>OS</th>
            <th>Agent Name</th>
            <th>IP</th>
            <th className=''>Date</th>
            <th className=''>Time</th>
            <th className=''>Status</th>
          </tr>
        </thead>
        <tbody>
          {filteredItems.map((item, index) => (
            <tr key={index}>
              <td>{index + 1}</td>
              <td>{highlightText(item.os_name, searchQuery)}</td>
              <td>{highlightText(item.agent_name, searchQuery)}</td>
              <td>{highlightText(item.registerIP, searchQuery)}</td>
              <td className=''>{convert(item.dateAdd)[0]}</td>
              <td className=''>{convert(item.dateAdd)[1]}</td>
              <td className=''>{item.status}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </>
  );
};

export default WazuhCombined;
