import React, { useState, useEffect } from 'react';
import { Chart } from 'react-google-charts';
import mygif from '../components/loader.gif';

const SiemPieChart = () => {
  const [pieChartData, setPieChartData] = useState({
    rule_description: [],
    bg_count: [],
    Color_codes: [],
    status: '',
  });

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      const response = await fetch('http://localhost:3001/get-pie_chart');
      const pieChartDataCount = await response.json();
      console.log(pieChartDataCount);
      setPieChartData(pieChartDataCount);
    } catch (error) {
      console.error(error);
    }
  };

  const chartData =
    pieChartData.rule_description &&
    pieChartData.bg_count &&
    pieChartData.rule_description.map((color, index) => [
      color
        .toString()
        .split(' ')
        .map((word) => word[0].toUpperCase() + word.substring(1))
        .join(' '),
      pieChartData.bg_count[index],
    ]);

  return (
    <div>
      {chartData.length > 0 ? (
        <Chart
          width={'600px'}
          height={'500px'}
          chartType="PieChart"
          loader={
            <div>
              <img
                src={mygif}
                alt="loading.."
                style={{ width: '500px', height: '500px' }}
              />
            </div>
          }
          data={[['Color', 'Count'], ...chartData]}
          options={{
            title: 'SIEM Alerts',
          }}
          rootProps={{ 'data-testid': '1' }}
        />
      ) : (
        <p>Loading...</p>
      )}
    </div>
  );
};

export default SiemPieChart;
