import React, { useState, useEffect } from 'react';
import { Chart } from 'react-google-charts';

const SiemPieChart = () => {
  const [pieChartData, setPieChartData] = useState({
    rule_description: [],
    bg_count: [],
    Color_codes: [],
    status: '',
  });

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      const response = await fetch("http://localhost:3001/get-pie_chart");
      const pieChartDataCount = await response.json();
      console.log(pieChartDataCount);
      setPieChartData(pieChartDataCount);
    } catch (error) {
      console.error(error);
    }
  };

  // Check if both rule_description and bg_count are present and have the same length
  const isValidData =
    pieChartData.rule_description &&
    pieChartData.bg_count &&
    pieChartData.rule_description.length === pieChartData.bg_count.length;

  const chartData = isValidData
    ? [['Color', 'Count']].concat(
        pieChartData.rule_description.map((color, index) => [
          color
            .toString()
            .split(' ')
            .map(word => word.charAt(0).toUpperCase() + word.slice(1))
            .join(' '),
          pieChartData.bg_count[index],
        ])
      )
    : [];

  return (
    <div>
      {pieChartData.Color_codes ? (
        <Chart
          width={'1000px'}
          height={'800px'}
          chartType="PieChart"
          loader={<div>Loading Chart</div>}
          data={chartData}
          options={{
            title: 'SIEM Alerts',
          }}
          rootProps={{ 'data-testid': '1' }}
        />
      ) : (
        <p>Loading...</p>
      )}
    </div>
  );
};

export default SiemPieChart;
